from flask import Flask, jsonifyfrom flask_cors import CORSimport jsonapp = Flask(__name__)CORS(app)# Load the JSON data from the filewith open('soccer_small.json') as file:    players_data = json.load(file)    #Endpoint to return all players and their attributes@app.route('/players', methods=['GET'])def get_players():    return jsonify(players_data)#Endpoint to return a specific player and all their attributes@app.route('/players/<name>', methods=['GET'])def get_individual_player(name):    player = next((p for p in players_data if p['Name'].lower().replace(' ','') == name.lower()),None)    return jsonify(player) if player else ('', 404)#Endpoint to return all clubs with a list of players playing for those clubs@app.route('/clubs', methods=['GET'])def get_clubs():    clubs ={}    for player in players_data:        club = player['Club']        if club not in clubs:            clubs[club] = []        clubs[club].append(player['Name'])    return jsonify(clubs)   #Endpoint to return a list of all attribute names@app.route('/attributes', methods=['GET'])def get_attributes():    if players_data:        return jsonify(list(players_data[0].keys()))if __name__ == '__main__':    app.run(host='0.0.0.0', port=5002)    